mapper.update:
update handler_task_status
set task_id = #{taskId,jdbcType=INTEGER},
  begin_index = #{beginIndex,jdbcType=BIGINT},
  end_index = #{endIndex,jdbcType=BIGINT},
  status = #{status,jdbcType=TINYINT}
where handler_id = #{handlerId,jdbcType=INTEGER}

mapper.select:
select ctr.table_name as tableName, hts.status as status from handler_task_status hts left join ht on hts.task_id = ht.task_id left join commit_table_record ctr on ht.commit_id=ctr.commit_id where 1=1
ctr.table_name like concat(#{tableName},'%')
hts.status = #{status}
